select (select real_name from t_user where user_id = #{userid}) name, (select count(1) from lm_client where sid = #{userid} and
to_char(QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
) total, (select count(1) from lm_client a where a.sid = #{userid} and
to_char(a.QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(a.QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
and (select count(1) from lm_clientrace b where b.t_id = a.id) > 0) reTotal from dual

select count(1) total,b.name from (select * from lm_client a left join (SELECT t.user_id,s.name FROM T_USER t left join t_organ_company s on t.organ_id=s.organ_id where t.organ_code='JY') b on a.rid = b.user_id) b where
to_char(QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
group by name

select count(1) reTotal,a.name from (select * from lm_client a left join (SELECT t.user_id,s.name FROM T_USER t left join t_organ_company s on t.organ_id=s.organ_id where t.organ_code='JY') b on a.rid = b.user_id) a where
to_char(QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
and (select count(1) from lm_clientrace b where b.t_id = a.id) > 0 group by name

select count(1) total,b.name,user_name,b.user_id from (select * from lm_client a left join (SELECT t.user_id,s.name,user_name FROM T_USER t left join t_organ_company s on t.organ_id=s.organ_id where t.organ_code='JY') b on a.rid = b.user_id) b where
to_char(QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
group by name,user_name,user_id

select count(1) reTotal,a.name,user_name,user_id from (select * from lm_client a left join (SELECT t.user_id,s.name,user_name FROM T_USER t left join t_organ_company s on t.organ_id=s.organ_id where t.organ_code='JY') b on a.rid = b.user_id) a where
to_char(QDATE, 'YYYY-MM-DD HH:mi') >=#{stime} AND to_char(QDATE, 'YYYY-MM-DD HH:mi') <=#{etime}
and (select count(1) from lm_clientrace b where b.t_id = a.id) > 0 group by name,user_name,user_id

select count(1),name,user_name,user_id from lm_client a right join (SELECT t.user_id,s.name,user_name FROM T_USER t join t_organ_company s on t.organ_id=s.organ_id
s.organ_code=#{orgcode} and
t.organ_id <> '00'
and t.organ_id= #{organId}
) b
on a.rid = b.user_id group by name,user_name,user_id order by name

select count(1),name,organ_id from lm_client a right join (SELECT t.user_id,s.name,user_name,t.organ_id FROM T_USER t join t_organ_company s on t.organ_id=s.organ_id
s.organ_code=#{orgcode} and
t.organ_id <> '00'
and t.organ_id= #{organId}
) b
on a.rid = b.user_id group by name,organ_id order by organ_id

select t.user_id ID,t.real_name||case t.status when '0' then '(离职)' end USER_NAME, o.name name, o.organ_id, t.Jz_Code CODE ,p.plan_num COUNT,p.month MONTH,p.create_date CREATEDATE from t_user t join t_user_role s on t.user_id = s.user_id join lm_sale_plan p ON t.user_id = p.user_id join t_organ_company o on o.organ_id = t.organ_id
s.role_id = '2' AND o.organ_code='JY'
and t.organ_id= #{organId}
and p.month= #{month}
