select a.companyid, b.name ABBR_NAME, sum(1) TOTNUM, sum(nvl((select 1 from lm_clientrace c where c.t_id = a.id and rownum=1),0)) YYNUM, sum(case a.idd when '1' then 1 else 0 end) DDNUM, sum(case a.is_income when '1' then 1 else 0 end) JJNUM, sum(case a.credit_status when '信审通过' then 1 when '合同审核中' then 1 when '签约中' then 1 when '交车完成' then 1 else 0 end) TGNUM, sum(case a.is_get_car when '1' then 1 else 0 end) TCNUM from lm_client a left join t_organ_company b on a.companyid = b.organ_id where a.fromtype = #{fromType,jdbcType=DECIMAL} and a.sex = '1' and a.companyid is not null and a.qdate between to_date(#{stime,jdbcType=VARCHAR}||' 00:00:00','yyyy-mm-dd hh24:mi:ss') and to_date(#{etime,jdbcType=VARCHAR}||' 23:59:59','yyyy-mm-dd hh24:mi:ss')
and a.deposit_status = #{depositStatus,jdbcType=VARCHAR}
and a.companyid in (select e.organ_id from t_organ_company e where e.status = '1' start with e.organ_id = #{organId,jdbcType=VARCHAR} connect by e.parent_id = prior e.organ_id) group by a.companyid, b.name order by a.companyid
