mapper.select:
select 
(select listagg(a.name,'-')  within group(order by a.code_level) from t_base_source a connect by prior a.pid=a.code start with a.code=t.from_type) FROM_TYPE_DESC,

from lm_client_import t

	
		and t.client_name = #{clientName,jdbcType=VARCHAR}
	
	
		and t.tel = #{tel,jdbcType=VARCHAR}
	
	
		and t.from_type_big = #{fromTypeBig,jdbcType=VARCHAR}
	
	
		and t.from_type like #{fromType,jdbcType=VARCHAR}||'%'
	
	
		and t.status = #{status,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO LM_CLIENT_IMPORT( ID, CLIENT_NAME, TEL, STATION_NO, CITY, FROM_TYPE_BIG, FROM_TYPE, STATUS, CREATED_TIME, CREATED_USER_ID, UPDATED_TIME, UPDATED_USER_ID, REMARK, CHANNEL, COUPONE_CODE ) SELECT S_CLIENT_IMPORT.NEXTVAL, T.CLIENT_NAME, T.TEL, T.STATION_NO, T.CITY, T.FROM_TYPE_BIG, T.FROM_TYPE, T.STATUS, T.CREATED_TIME, T.CREATED_USER_ID, T.UPDATED_TIME, T.UPDATED_USER_ID, T.REMARK, T.CHANNEL, T.COUPONE_CODE FROM (
(SELECT #{item.clientName:VARCHAR} AS CLIENT_NAME, #{item.tel:VARCHAR} AS TEL, #{item.stationNo:VARCHAR} AS STATION_NO, #{item.city:VARCHAR} AS CITY, #{item.fromTypeBig:VARCHAR} AS FROM_TYPE_BIG, #{item.fromType:VARCHAR} AS FROM_TYPE, #{item.status:VARCHAR} AS STATUS, #{item.createdTime:TIMESTAMP} AS CREATED_TIME, #{item.createdUserId:DECIMAL} AS CREATED_USER_ID, #{item.updatedTime:TIMESTAMP} AS UPDATED_TIME, #{item.updatedUserId:DECIMAL} AS UPDATED_USER_ID, #{item.remark:VARCHAR} AS REMARK, #{item.channel:VARCHAR} AS CHANNEL, #{item.couponeCode:VARCHAR} AS COUPONE_CODE FROM DUAL)
) T

mapper.select:
SELECT COUNT(ID) AS NUM FROM (
	SELECT A.ID FROM LM_CLIENT_IMPORT A WHERE A.TEL=#{tel:VARCHAR} 
	UNION ALL 
	SELECT B.ID FROM LM_CLIENT B WHERE B.TEL=#{tel:VARCHAR}
) T
